    private void jButton1ActionPerformed(java.awt.event.ActionEvent evt) {//GEN-FIRST:event_jButton1ActionPerformed
        String name = jTextField1.getText();
        String pwd = jTextField2.getText().toString();
        int clearance = 0;
        if (name.isEmpty() || pwd.isEmpty() || jTextField3.getText().isEmpty()) {
            JOptionPane.showMessageDialog(rootPane, "Please enter a valid input.", "Bad input", WIDTH);
        } else {
            clearance = Integer.parseInt(jTextField3.getText());
        }
        SQLConnectEmp.editUser(name, pwd, clearance);
        UpdatejTable1();
    }//GEN-LAST:event_jButton1ActionPerformed

