    private void jButton3ActionPerformed(java.awt.event.ActionEvent evt) {//GEN-FIRST:event_jButton3ActionPerformed

        if (jTextField1.getText().isEmpty()) {
            JOptionPane.showMessageDialog(rootPane, "Please enter a valid input", "Error", WIDTH);
        } else if (jTextField2.getText().isEmpty()) {
            JOptionPane.showMessageDialog(rootPane, "Please enter a valid input", "Error", WIDTH);
        } else if (jTextField3.getText().isEmpty()) {
            JOptionPane.showMessageDialog(rootPane, "Please enter a valid input", "Error", WIDTH);
        } else if (Integer.parseInt(jTextField3.getText()) > 2) {
            JOptionPane.showMessageDialog(rootPane, "Please enter a valid clearance", "Error", WIDTH);
        } else {
            String name = jTextField1.getText();
            String Password = jTextField2.getText();
            int clearnc = Integer.parseInt(jTextField3.getText());
            SQLConnectEmp.createNewUser(name, Password, clearnc, new BigDecimal(0));
            UpdatejTable1();
        }


    }//GEN-LAST:event_jButton3ActionPerformed

