    /**
     * Sets the jTable1 to the specified model. Used to update the table
     * automatically
     */
    private void UpdatejTable1() {
        DefaultTableModel modelUsers = new DefaultTableModel();
        modelUsers.setColumnIdentifiers(new String[]{"ID", "Login", "Password", "Clearance"});
        for (User U : SQLConnectEmp.getUsersFromDB()) {
            modelUsers.addRow(new String[]{Integer.toString(U.getId()), U.getLogin(), U.getPassword(), Integer.toString(U.getClearance())});
        }
        jTable1.setModel(modelUsers);

    }

